options.customCss:
:root {
	/* Typography - Apple System Fonts */
	--font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-weight-semibold: 500;
	--font-size-body: 15px;
	--font-size-label: 17px;
	--font-size-test-notice: 14px;
	--font-size-input: 17px;
	--font-size-header: 32px;
	--font-size-paragraph: 17px;
	--font-size-link: 15px;
	--font-size-error: 14px;
	--font-size-html-h1: 48px;
	--font-size-html-h2: 32px;
	--font-size-html-h3: 24px;
	--font-size-html-h4: 20px;
	--font-size-html-h5: 17px;
	--font-size-html-h6: 15px;
	--font-size-subheader: 20px;

	/* Colors - Apple Inspired Light Theme */
	--color-background: #ffffff;
	--color-secondary-background: #f8f9fa;
	--color-test-notice-text: #d68900;
	--color-test-notice-bg: rgba(255, 193, 7, 0.1);
	--color-test-notice-border: rgba(255, 193, 7, 0.3);
	--color-card-bg: #ffffff;
	--color-card-border: #d2d2d7;
	--color-card-shadow: rgba(0, 0, 0, 0.1);
	--color-link: #007aff;
	--color-header: #1d1d1f;
	--color-subheader: #86868b;
	--color-label: #1d1d1f;
	--color-input-border: #d2d2d7;
	--color-input-text: #1d1d1f;
	--color-input-bg: #f8f9fa;
	--color-focus-border: #007aff;
	--color-submit-btn-bg: #007aff;
	--color-submit-btn-text: #ffffff;
	--color-error: #ff3b30;
	--color-required: #ff3b30;
	--color-clear-button-bg: #86868b;
	--color-html-text: #1d1d1f;
	--color-html-link: #007aff;
	--color-header-subtext: #86868b;
	--color-placeholder: #86868b;

	/* Border Radii - Apple's Rounded Design */
	--border-radius-card: 24px;
	--border-radius-input: 12px;
	--border-radius-button: 12px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 24px;

	/* Spacing - More Generous Apple-style */
	--padding-container-top: 40px;
	--padding-card: 32px;
	--padding-test-notice-vertical: 16px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 24px;
	--padding-form-input: 16px 20px;
	--card-padding: 32px;
	--card-margin-bottom: 24px;
	--spacing-small: 8px;
	--spacing-medium: 16px;
	--spacing-large: 24px;
	--spacing-xl: 32px;

	/* Dimensions */
	--container-width: 600px;
	--submit-btn-height: 56px;
	--checkbox-size: 20px;
	--input-height: 56px;

	/* Effects */
	--box-shadow-card: 0 8px 32px var(--color-card-shadow);
	--box-shadow-input-focus: 0 0 0 4px rgba(0, 122, 255, 0.1);
	--opacity-placeholder: 0.6;
	--transition-all: all 0.3s ease;
}

/* Dark Mode Support */
@media (prefers-color-scheme: dark) {
	:root {
		--color-background: #000000;
		--color-secondary-background: #1c1c1e;
		--color-test-notice-text: #ffc107;
		--color-test-notice-bg: rgba(255, 193, 7, 0.1);
		--color-test-notice-border: rgba(255, 193, 7, 0.2);
		--color-card-bg: #2c2c2e;
		--color-card-border: #38383a;
		--color-card-shadow: rgba(0, 0, 0, 0.3);
		--color-link: #0a84ff;
		--color-header: #ffffff;
		--color-subheader: #8e8e93;
		--color-label: #ffffff;
		--color-input-border: #38383a;
		--color-input-text: #ffffff;
		--color-input-bg: #1c1c1e;
		--color-focus-border: #0a84ff;
		--color-submit-btn-bg: #0a84ff;
		--color-clear-button-bg: #8e8e93;
		--color-html-text: #ffffff;
		--color-html-link: #0a84ff;
		--color-placeholder: #8e8e93;
	}
}

/* Global Styles */
* {
	box-sizing: border-box;
}

body {
	font-family: var(--font-family);
	background: var(--color-background);
	color: var(--color-html-text);
	line-height: 1.47;
	margin: 0;
	padding: 0;
	transition: var(--transition-all);
}

/* Container */
.form-container {
	max-width: var(--container-width);
	margin: 0 auto;
	padding: var(--padding-container-top) var(--spacing-large);
}

/* Header Styles */
.form-header {
	text-align: center;
	margin-bottom: var(--spacing-xl);
}

.form-header h1 {
	font-size: var(--font-size-header);
	font-weight: 700;
	letter-spacing: -0.025em;
	color: var(--color-header);
	margin: 0 0 var(--spacing-medium) 0;
	background: linear-gradient(135deg, var(--color-submit-btn-bg), #5856d6);
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
	background-clip: text;
}

.form-header p,
.form-header .subheader {
	font-size: var(--font-size-subheader);
	color: var(--color-subheader);
	margin: 0;
	font-weight: var(--font-weight-normal);
}

/* Card Styles */
.form-card,
.card {
	background: var(--color-card-bg);
	border: 1px solid var(--color-card-border);
	border-radius: var(--border-radius-card);
	padding: var(--card-padding);
	margin-bottom: var(--card-margin-bottom);
	box-shadow: var(--box-shadow-card);
	transition: var(--transition-all);
}

.form-card:hover,
.card:hover {
	transform: translateY(-2px);
	box-shadow: 0 12px 40px var(--color-card-shadow);
}

/* Test Notice */
.test-notice {
	background: var(--color-test-notice-bg);
	border: 1px solid var(--color-test-notice-border);
	border-radius: var(--border-radius-input);
	padding: var(--padding-test-notice-vertical) var(--padding-test-notice-horizontal);
	margin-bottom: var(--spacing-large);
	color: var(--color-test-notice-text);
	font-size: var(--font-size-test-notice);
	font-weight: var(--font-weight-semibold);
}

/* Form Groups */
.form-group {
	margin-bottom: var(--spacing-large);
}

.form-group:last-child {
	margin-bottom: 0;
}

/* Labels */
label,
.form-label {
	display: block;
	font-size: var(--font-size-label);
	font-weight: var(--font-weight-bold);
	color: var(--color-label);
	margin-bottom: var(--spacing-small);
	line-height: 1.3;
}

.required-indicator {
	color: var(--color-required);
	font-weight: var(--font-weight-bold);
}

/* Input Styles */
input[type="text"],
input[type="email"],
input[type="tel"],
input[type="url"],
input[type="password"],
input[type="number"],
input[type="date"],
input[type="time"],
input[type="datetime-local"],
select,
textarea {
	width: 100%;
	height: var(--input-height);
	padding: var(--padding-form-input);
	border: 2px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	background: var(--color-input-bg);
	color: var(--color-input-text);
	font-size: var(--font-size-input);
	font-family: var(--font-family);
	transition: var(--transition-all);
	-webkit-appearance: none;
	appearance: none;
}

textarea {
	height: auto;
	min-height: 120px;
	resize: vertical;
	line-height: 1.5;
}

input:focus,
select:focus,
textarea:focus {
	outline: none;
	border-color: var(--color-focus-border);
	box-shadow: var(--box-shadow-input-focus);
	background: var(--color-card-bg);
}

input::placeholder,
textarea::placeholder {
	color: var(--color-placeholder);
	opacity: var(--opacity-placeholder);
}

/* Select Dropdown */
select {
	background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg xmlns='http://www.w3.org/2000/svg' fill='%23999' viewBox='0 0 16 16'%3E%3Cpath d='m8 10.293 3.146-3.147a.5.5 0 0 1 .708.708l-3.5 3.5a.5.5 0 0 1-.708 0l-3.5-3.5a.5.5 0 1 1 .708-.708L8 10.293z'/%3E%3C/svg%3E");
	background-repeat: no-repeat;
	background-position: right 16px center;
	background-size: 16px;
	padding-right: 48px;
}

/* Checkbox and Radio */
input[type="checkbox"],
input[type="radio"] {
	width: var(--checkbox-size);
	height: var(--checkbox-size);
	margin-right: var(--spacing-small);
	accent-color: var(--color-submit-btn-bg);
	transform: scale(1.2);
}

.checkbox-group,
.radio-group {
	display: flex;
	align-items: center;
	margin-bottom: var(--spacing-medium);
}

.checkbox-group label,
.radio-group label {
	margin-bottom: 0;
	font-weight: var(--font-weight-normal);
	cursor: pointer;
	display: flex;
	align-items: center;
}

/* Submit Button */
.submit-btn,
button[type="submit"],
.btn-primary {
	width: 100%;
	height: var(--submit-btn-height);
	background: var(--color-submit-btn-bg);
	color: var(--color-submit-btn-text);
	border: none;
	border-radius: var(--border-radius-button);
	font-size: var(--font-size-input);
	font-weight: var(--font-weight-bold);
	font-family: var(--font-family);
	cursor: pointer;
	transition: var(--transition-all);
	display: flex;
	align-items: center;
	justify-content: center;
	text-decoration: none;
	margin-top: var(--spacing-large);
}

.submit-btn:hover,
button[type="submit"]:hover,
.btn-primary:hover {
	background: var(--color-focus-border);
	transform: translateY(-2px);
	box-shadow: 0 8px 24px rgba(0, 122, 255, 0.3);
}

.submit-btn:active,
button[type="submit"]:active,
.btn-primary:active {
	transform: translateY(0);
}

/* Error Messages */
.error-message,
.field-error {
	color: var(--color-error);
	font-size: var(--font-size-error);
	margin-top: var(--spacing-small);
	font-weight: var(--font-weight-semibold);
	display: flex;
	align-items: center;
	gap: var(--spacing-small);
}

.error-message::before,
.field-error::before {
	content: "‚ö†Ô∏è";
}

/* Links */
a {
	color: var(--color-link);
	text-decoration: none;
	font-weight: var(--font-weight-semibold);
	transition: var(--transition-all);
}

a:hover {
	opacity: 0.8;
	text-decoration: underline;
}

/* Clear Button */
.clear-button {
	background: var(--color-clear-button-bg);
	color: white;
	border: none;
	border-radius: var(--border-radius-clear-btn);
	width: 24px;
	height: 24px;
	cursor: pointer;
	transition: var(--transition-all);
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 12px;
}

.clear-button:hover {
	opacity: 0.8;
	transform: scale(1.1);
}

/* Responsive Design */
@media (max-width: 768px) {
	.form-container {
		padding: var(--spacing-large) var(--spacing-medium);
	}

	.form-header h1 {
		font-size: 28px;
	}

	.card-padding {
		padding: var(--spacing-large);
	}

	.form-card,
	.card {
		padding: var(--spacing-large);
	}
}

/* Additional Form Elements */
.form-row {
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: var(--spacing-medium);
}

@media (max-width: 640px) {
	.form-row {
		grid-template-columns: 1fr;
	}
}

.help-text {
	font-size: 14px;
	color: var(--color-subheader);
	margin-top: var(--spacing-small);
}

/* File Upload */
input[type="file"] {
	padding: var(--spacing-medium);
	border: 2px dashed var(--color-input-border);
	background: var(--color-secondary-background);
	cursor: pointer;
}

input[type="file"]:hover {
	border-color: var(--color-focus-border);
	background: var(--color-card-bg);
}

/* Progress Indicators */
.progress-bar {
	width: 100%;
	height: 8px;
	background: var(--color-secondary-background);
	border-radius: 4px;
	overflow: hidden;
	margin: var(--spacing-medium) 0;
}

.progress-fill {
	height: 100%;
	background: linear-gradient(90deg, var(--color-submit-btn-bg), #5856d6);
	transition: width 0.3s ease;
}

.n8n-link {
  display: none;
}
